import React, { useState } from 'react';
import './App.css';
import { GoogleLogin } from '@react-oauth/google';

const images = [
  '/image1.jpg', // Add your image file paths here
  '/image2.jpg', // Make sure these files exist in the public folder
  '/image3.jpg'
];

function App() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePreviousImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  const handleLoginSuccess = (credentialResponse) => {
    console.log('Login Success:', credentialResponse);
    // Handle the login response, e.g., saving the user info to your state or server
  };

  const handleLoginFailure = (error) => {
    console.error('Login Failed:', error);
    // Handle the error here
  };

  return (
    <div className="app-container">
      <div className="left-side">
        <div className="image-slider">
          <img
            src={images[currentImageIndex]}
            alt="Sliding content"
            className="slider-image"
          />
          <div className="navigation">
            <button onClick={handlePreviousImage} className="nav-button">
              ◀
            </button>
            <button onClick={handleNextImage} className="nav-button">
              ▶
            </button>
          </div>
        </div>
      </div>
      <div className="right-side">
        <div className="card">
          <div className="logo">
            <img src="/paw.png" alt="Company Logo" className="paw-icon" />
          </div>
          <h1 className="company-name">Company name</h1>
          <p className="tagline">Create your free account</p>
          <div className="google-login-button">
            <GoogleLogin
              onSuccess={handleLoginSuccess}
              onError={handleLoginFailure}
            />
          </div>
          <p className="terms">
            By logging in or signing up, you agree to our policies, including our
            <a href="#"> Terms of Service </a> and <a href="#"> Privacy Policy </a>
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
