import React, { useState, useEffect } from 'react';
import './App.css';
import { GoogleLogin } from '@react-oauth/google';
const images = [
  '/image_1.png', // Add your image file paths here
  '/image_2.png', // Make sure these files exist in the public folder
  '/image_3.png'
];

function App() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [direction, setDirection] = useState('next');

  // Automatically change the image every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setDirection('next');
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000); // Change image every 3000 ms (3 seconds)

    return () => clearInterval(interval);
  }, []);

  const handleDotClick = (index) => {
    setDirection(index > currentImageIndex ? 'next' : 'prev');
    setCurrentImageIndex(index);
  };

  const handlePreviousImage = () => {
    setDirection('prev');
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  const handleNextImage = () => {
    setDirection('next');
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  return (
    <div className="app-container">
      <div className="left-side">
        <div className={`image-slider ${direction}`}>
          <img
            src={images[currentImageIndex]}
            alt="Sliding content"
            className="slider-image"
          />
          <div className="dots">
            {images.map((_, index) => (
              <span
                key={index}
                className={`dot ${index === currentImageIndex ? 'active' : ''}`}
                onClick={() => handleDotClick(index)}
              ></span>
            ))}
          </div>
          <button onClick={handlePreviousImage} className="prev-arrow">
            &#8249;
          </button>
          <button onClick={handleNextImage} className="next-arrow">
            &#8250;
          </button>
        </div>
      </div>
      <div className="right-side">
        <div className="card">
          <div className="logo">
            <img src="/paw.png" alt="Company Logo" className="paw-icon" />
          </div>
          <h1 className="company-name">Company name</h1>
          <p className="tagline">Create your free account</p>
          <div className="google-login-button">
            <GoogleLogin
              onSuccess={handleLoginSuccess}
              onError={handleLoginFailure}
            />
          </div>
          <p className="terms">
            By logging in or signing up, you agree to our policies, including our
            <a href="#"> Terms of Service </a> and <a href="#"> Privacy Policy </a>
          </p>
        </div>
      </div>
    </div>
  );
}

const handleLoginSuccess = (credentialResponse) => {
  console.log('Login Success:', credentialResponse);
};

const handleLoginFailure = (error) => {
  console.error('Login Failed:', error);
};

export default App;
