import React, { useState, useEffect } from 'react';
import './App.css';
import { GoogleLogin } from '@react-oauth/google';

const images = [
  '/image_1.png', // Add your image file paths here
  '/image_2.png', // Make sure these files exist in the public folder
  '/image_3.png'
];


function App() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isSliding, setIsSliding] = useState(false);

  // Automatically change the image every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      handleNextImage();
    }, 3000); // Change image every 3000 ms (3 seconds)

    return () => clearInterval(interval);
  }, [currentImageIndex]);

  const handleNextImage = () => {
    if (isSliding) return;

    setIsSliding(true);
    setTimeout(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
      setIsSliding(false);
    }, 500); // Duration should match the CSS transition
  };

  const handlePreviousImage = () => {
    if (isSliding) return;

    setIsSliding(true);
    setTimeout(() => {
      setCurrentImageIndex((prevIndex) =>
        prevIndex === 0 ? images.length - 1 : prevIndex - 1
      );
      setIsSliding(false);
    }, 500); // Duration should match the CSS transition
  };

  const handleDotClick = (index) => {
    setCurrentImageIndex(index);
  };

  return (
    <div className="app-container">
      <div className="left-side">
        <div className="image-slider-wrapper">
          <div className={`image-slider ${isSliding ? 'sliding' : ''}`}>
            {images.map((src, index) => (
              <img
                key={index}
                src={src}
                alt={`Sliding content ${index}`}
                className={`slider-image ${index === currentImageIndex ? 'visible' : ''}`}
              />
            ))}
          </div>
          <button onClick={handlePreviousImage} className="center-arrow">
            &#8249;
          </button>
        </div>
        <div className="dots">
          {images.map((_, index) => (
            <span
              key={index}
              className={`dot ${index === currentImageIndex ? 'active' : ''}`}
              onClick={() => handleDotClick(index)}
            ></span>
          ))}
        </div>
      </div>
      <div className="right-side">
        <div className="card">
          <div className="logo">
            <img src="/paw.png" alt="Company Logo" className="paw-icon" />
          </div>
          <h1 className="company-name">Company name</h1>
          <p className="tagline">Create your free account</p>
          <div className="google-login-button">
            <GoogleLogin
              onSuccess={(response) => console.log('Login Success:', response)}
              onError={(error) => console.error('Login Failed:', error)}
            />
          </div>
          <p className="terms">
            By logging in or signing up, you agree to our policies, including our
            <a href="#"> Terms of Service </a> and <a href="#"> Privacy Policy </a>
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
